mapper.update:
update HB_Station set 
subject=#{subject}, content=#{content}
where num=#{num}

mapper.select:
select count(*) from HB_StationReply 
where ref_num = #{ref_num} 
and re_step = #{re_step} + 1 
and re_level > #{re_level}
